package com.example.homeworke4.COUNTRY;

import android.os.Bundle;

import androidx.annotation.NonNull;
import androidx.annotation.Nullable;
import androidx.fragment.app.Fragment;

import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;

import com.example.homeworke4.CONTINENT.ContinentAdapter;
import com.example.homeworke4.CONTINENT.ContinentModel;
import com.example.homeworke4.databinding.FragmentContinentBinding;
import com.example.homeworke4.databinding.FragmentCountryBinding;

import java.util.ArrayList;

public class CountryFragment extends Fragment {

    private FragmentCountryBinding binding;
    private ArrayList<CountryModel> countryList = new ArrayList<>();
    private Integer position;

    @Override
    public View onCreateView(LayoutInflater inflater, ViewGroup container,
                             Bundle savedInstanceState) {
        binding = FragmentCountryBinding.inflate(getLayoutInflater());
        return binding.getRoot();
    }

    @Override
    public void onViewCreated(@NonNull View view, @Nullable Bundle savedInstanceState) {
        super.onViewCreated(view, savedInstanceState);
        position = getArguments().getInt("country");
        checkPosition(position);
        CountryAdapter adapter = new CountryAdapter(countryList);
        binding.rvCountryName.setAdapter(adapter);
    }

    private void checkPosition(int position) {
        if (position == 0){
            loadAsia();
        } else if (position == 1){
            loadEurope();
        } else if (position == 2){
            loadNorthAmerica();
        } else if (position == 3){
            loadSouthAmerica();
        } else if (position == 4){
            loadAfrica();
        } else if (position == 5){
            loadAustralia();
        }
    }

    private void loadAustralia() {
        countryList.add(new CountryModel("data:image/png;base64,","Вануату"));
        countryList.add(new CountryModel("data:image/png;base64,","Кирибати"));
        countryList.add(new CountryModel("data:image/png;base64,","Нару"));
        countryList.add(new CountryModel("data:image/png;base64,","Новая Зеландия"));
        countryList.add(new CountryModel("data:image/png;base64,","Палау"));

    }

    private void loadEurope() {
        countryList.add(new CountryModel("data:image/png;base64,", "Германия"));
        countryList.add(new CountryModel("data:image/png;base64,","Ирландия"));
        countryList.add(new CountryModel("data:image/png;base64,","Греция"));
        countryList.add(new CountryModel("data:image/png;base64,","Великобритания"));
        countryList.add(new CountryModel("data:image/png;base64,","Франция"));

    }

    private void loadAfrica() {
        countryList.add(new CountryModel("https://upload.wikimedia.org/wikipedia/commons/7/79/Flag_of_Nigeria.svg","Нигерия"));
        countryList.add(new CountryModel("https://upload.wikimedia.org/wikipedia/commons/4/49/Flag_of_Kenya.svg","Кения"));
        countryList.add(new CountryModel("https://upload.wikimedia.org/wikipedia/commons/2/2c/Flag_of_Morocco.svg","Марокко"));
        countryList.add(new CountryModel("https://upload.wikimedia.org/wikipedia/commons/1/19/Flag_of_Ghana.svg","Гана"));
        countryList.add(new CountryModel("https://upload.wikimedia.org/wikipedia/commons/4/4e/Flag_of_Uganda.svg","Уганда"));

    }

    private void loadSouthAmerica() {
        countryList.add(new CountryModel("https://upload.wikimedia.org/wikipedia/commons/0/05/Flag_of_Brazil.svg","Бразилия"));
        countryList.add(new CountryModel("https://upload.wikimedia.org/wikipedia/commons/1/1a/Flag_of_Argentina.svg","Аргентина"));
        countryList.add(new CountryModel("https://upload.wikimedia.org/wikipedia/commons/thumb/8/82/Flag_of_Peru_%28war%29.svg/200px-Flag_of_Peru_%28war%29.svg.png","Перу"));
        countryList.add(new CountryModel("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAPoAAACnCAMAAAAPIrEmAAAAD1BMVEX/zQAAMIfIEC5/fkSXGERJ7nDkAAAAh0lEQVR4nO3PARGAMAADsQ7wr3k6uE8cZAMAAAAAAAAAAAAAAAAAAAAAAAAA+Ls3aydLvUi9SL1IvUi9SL1IvUi9SL1IvUi9SL1IvUi9SL1IvUi9SL1IvUi9aF/Wniz1IvUi9SL1IvUi9SL1IvUi9SL1IvUi9SL1IvUi9SL1IvUi9SL1IvWiC49WfvVUEaA/AAAAAElFTkSuQmCC","Колумбия"));
        countryList.add(new CountryModel("data:image/png;base64,","Чили"));

    }

    private void loadNorthAmerica() {
        countryList.add(new CountryModel("https://upload.wikimedia.org/wikipedia/commons/a/a4/Flag_of_the_United_States.svg","С.Ш.А"));
        countryList.add(new CountryModel("https://upload.wikimedia.org/wikipedia/commons/d/d9/Flag_of_Canada_%28Pantone%29.svg","Канада"));
        countryList.add(new CountryModel("https://upload.wikimedia.org/wikipedia/commons/f/fc/Flag_of_Mexico.svg","Мексика"));
        countryList.add(new CountryModel("https://upload.wikimedia.org/wikipedia/commons/0/09/Flag_of_Greenland.svg","Гренландия"));
        countryList.add(new CountryModel("https://upload.wikimedia.org/wikipedia/commons/0/0a/Flag_of_Jamaica.svg","Ямайка"));

    }

    private void loadAsia() {
        countryList.add(new CountryModel("data:image/png;base64,","Кыргызстан"));
        countryList.add(new CountryModel("data:image/png;base64,","Казахстан"));
        countryList.add(new CountryModel("https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSvv6I2tkUIwxIbfDZYKfQqpoCb-SHP8BE87WMIljh8Cg&s","Китай"));
        countryList.add(new CountryModel("data:image/png;base64,","Япония"));
        countryList.add(new CountryModel("data:image/png;base64,","Турция"));

    }
}